CREATE TABLE IF NOT EXISTS `artist` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nomartist` varchar(255) CHARACTER SET latin1 NOT NULL,
  `nom` varchar(255) CHARACTER SET latin1 NOT NULL,
  `neele` date NOT NULL,
  `neea` varchar(255) CHARACTER SET latin1 NOT NULL,
  `mortle` date NOT NULL,
  `morta` varchar(255) CHARACTER SET latin1 NOT NULL,
  `biography` text CHARACTER SET latin1 NOT NULL,
  `auteur` varchar(255) NOT NULL DEFAULT 'HariS Seldon',
  `creerle` datetime NOT NULL,
  `typeartist` varchar(255) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `nomartist` (`nomartist`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=42 ;

