--
-- Structure de la table `enregistrement`
--

CREATE TABLE IF NOT EXISTS `enregistrement` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `idartist` int(11) NOT NULL,
  `interpretes` text NOT NULL,
  `duree` time NOT NULL,
  `annee` date NOT NULL,
  `type` varchar(255) NOT NULL,
  `instruments` text NOT NULL,
  `titre` varchar(500) NOT NULL,
  `opus` int(11) NOT NULL,
  `histoire` text NOT NULL,
  `auteur` varchar(255) NOT NULL DEFAULT 'HariS Seldon',
  `creerle` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;

