INSERT INTO `evenement` (`id`, `nom`, `date`, `lieu`, `detail`, `auteur`) VALUES
(1, 'Programme des concerts à l''Auditorium de Lyon', '0000-00-00', 'Auditorium Orchestre National‎ 149 Rue Garibaldi 69003 Lyon', '<table style="width: 760px;" border="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td valign="top"><span class="entree_txt_16">Septembre</span></td>\r\n<td>\r\n<p><span class="date">Mardi 04 septembre - 20h00</span><br /><span class="date">Mercredi 05 septembre - 20h00</span><br /><span class="Style1">Concerts d''ouverture</span></p>\r\n<p><span class="Style2">REQUIEM DE BERLIOZ</span></p>\r\n<p><span class="Style1"><strong>Hector Berlioz</strong>&nbsp;Grande Messe des morts (Requiem)</span></p>\r\n<p><span class="Style1">Orchestre national de Lyon<br />Steve Davislim, t&eacute;nor<br />Choral Arts Society of Washington<br />Norman Scribner, chef de choeur<br />Philharmonia Chorus<br />Stefan Bevier, chef de choeur associ&eacute;<br />Choeurs et Solistes de Lyon-Bernard T&eacute;tu (Choeur d&rsquo;oratorio de Lyon)<br />Catherine Molmerret, chef de choeur<br />Leonard Slatkin, direction</span></p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /><br /><span class="date">Vendredi 14 septembre - 20</span><span class="date">h30</span><br /><span class="date">Samedi 15 septembre - 20</span><span class="date">h30</span><br /><span class="date">Dimanche 16 septembre - 17</span><span class="date">h00</span><br /><span class="Style1">spectacle propos&eacute; dans le cadre de la 15e biennale de la Danse de Lyon</span></p>\r\n<p align="justify"><span class="Style1"><span class="Style2">BALI DANSES ET DRAMES</span><br /><br />Bali danses et drames&nbsp;<br />Ce spectacle, nous plonge dans la culture balinaise, dans ce qu&rsquo;elle a de plus profond, de plus r&eacute;el et de plus authentique. Nous aurons la chance de d&eacute;couvrir ces arts chor&eacute;graphiques et dramatiques qui ont inspir&eacute; dans les ann&eacute;es trente des artistes aussi diff&eacute;rents que Charlie Chaplin, Benjamin Britten ou Antonin Artaud.</span></p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /></p>\r\n<p><span class="date">Jeudi 20 septembre - 20</span><span class="date">h00</span><br /><span class="date">Samedi 22 septembre - 18</span><span class="date">h00</span><br /><span class="Style1">Symphonique ONL</span></p>\r\n<p class="Style2">DVORAK PAR TRULS MORK</p>\r\n<p class="Style1"><strong>Anton&iacute;n Dvor&aacute;k</strong>&nbsp;Concerto pour violoncelle en si mineur, op. 104<br /><strong>Arnold Sch&ouml;nberg</strong>&nbsp;Pell&eacute;as et M&eacute;lisande, po&egrave;me symphonique op. 5<br /><br />Orchestre national de Lyon<br />Truls M&oslash;rk, violoncelle<br />Josep Pons, direction</p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /><br /><span class="date">Jeudi 27 septembre - 20h00</span><br /><span class="Style1">Symphonique ONL</span></p>\r\n<p><span class="Style2">4e CONCERTO DE BEETHOVEN</span></p>\r\n<p class="Style1"><strong>Ludwig van Beethoven</strong>&nbsp;Coriolan, ouverture op. 62 -&nbsp;<br />- Concerto pour piano n&deg; 4, en sol majeur, op. 58<br /><strong>Karol Szymanowski</strong>&nbsp;Le Prince Potemkine, op. 51<br /><strong>Witold Lutoslawski</strong>&nbsp;Concerto pour orchestre</p>\r\n<p class="Style1">Orchestre national de Lyon<br />Martin Helmchen, piano<br />Antoni Wit, direction</p>\r\n<p class="Style1" align="right"><a href="http://www.auditoriumlyon.com/onl_info/Resume_concerts/2012-2013/4e_concerto_de_Beethoven.htm" target="_blank">&nbsp;</a></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td colspan="2" valign="top"><img src="http://www.auditoriumlyon.com/onl_info/images/760_filet.jpg" alt="trait" width="760" height="20" /></td>\r\n</tr>\r\n<tr>\r\n<td valign="top"><a id="avr7" name="oct"></a><span class="entree_txt_16">Octobre</span></td>\r\n<td>\r\n<p><span class="date">Jeudi 04 octobre - 20h00</span><br /><span class="date">Samedi 06 octobre - 18h00</span><br /><span class="Style1">Symphonique ONL</span></p>\r\n<p><span class="Style2">PIERRE-LAURENT AIMARD, BENJAMIN ET BEETHOVEN</span></p>\r\n<p class="Style1"><strong>Benjamin Britten</strong>&nbsp;&laquo;Quatre Interludes marins&raquo; de Peter Grimes&nbsp;<br /><strong>George Benjamin</strong>&nbsp;Duet, pour piano et orchestre<br /><strong>Ludwig van Beethoven</strong>&nbsp;Concerto pour piano n&deg; 2, en si b&eacute;mol majeur, op. 19<br /><strong>Albert Roussel</strong>&nbsp;Bacchus et Ariane, suite n&deg; 2</p>\r\n<p class="Style1">Orchestre national de Lyon<br />Pierre-Laurent Aimard, piano<br />Martyn Brabbins, direction</p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /><br /><span class="date">Dimanche 07 octobre - 16</span><span class="date">h00</span><br /><span class="Style1">Orgue</span></p>\r\n<p class="Style2">JOUTE D''IMPROVISATION</p>\r\n<p class="Style1"><strong>Paul Goussot, Samuel Li&eacute;geon</strong>&nbsp;et&nbsp;<br /><strong>Baptiste-Florian Marle-Ouvrard</strong>, orgue &agrave; six, quatre et deux mains, piano et clavecin</p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /><br /><span class="date">Jeudi 11 octobre - 20h00</span><br /><span class="date">Samedi 13 octobre - 18h00</span><br /><span class="Style1">Symphonique ONL</span></p>\r\n<p><span class="Style2">REQUIEM DE DVORAK</span></p>\r\n<p class="Style1"><strong>Anton&iacute;n Dvor&aacute;k&nbsp;</strong>Requiem, op. 89</p>\r\n<p class="Style1">Orchestre national de Lyon<br />Choeur philharmonique slovaque<br />Blanka Juhan&aacute;kov&aacute;, chef de choeur<br />Simona Saturov&aacute;, soprano<br />Maria Radner, mezzo-soprano<br />Charles Reid, t&eacute;nor<br />Jan Stava, basse<br />Tom&aacute;s Netopil, direction</p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /><br /><span class="date">Vendredi 12 octobre - 15</span><span class="date">h00</span><br /><span class="Style1">Concert Expresso</span></p>\r\n<p class="Style2">CHOEUR SLOVAQUE</p>\r\n<p class="Style1"><strong>Sergue&iuml; Rachmaninov</strong>&nbsp;Les Vigiles, op. 37<br /><strong>Francis Poulenc</strong>&nbsp;Quatre Petites Pri&egrave;res de saint Fran&ccedil;ois d&rsquo;Assise<br /><strong>Zdenek Luk&aacute;s</strong>&nbsp;Hommage aux cr&eacute;ateurs, op. 147, d&rsquo;apr&egrave;s des po&egrave;mes de V&aacute;clav Fischer</p>\r\n<p class="Style1">Choeur philharmonique slovaque<br />Blanka Juhan&aacute;kov&aacute;, chef de choeur<br />Tom&aacute;s Nemec, piano<br />Yvetta Slez&aacute;kov&aacute;, violon<br />Michal Seredic, t&eacute;nor<br />Fran&ccedil;ois Castang, pr&eacute;sentation</p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /><br /><span class="date">Samedi 20 octobre - 15h00</span><br /><span class="Style1">Concert Jeune Public</span></p>\r\n<p><span class="Style2">LAC DES CYGNES EN LUMIERE</span></p>\r\n<p class="Style1"><strong>Piotr Ilitch Tcha&iuml;kovski</strong>&nbsp;Le Lac des cygnes, suite op. 20a<br />(arrangement de Vladimir Fedosse&iuml;ev)</p>\r\n<p class="Style1">Orchestre national de Lyon<br />H&eacute;l&egrave;ne Bouchez, direction</p>\r\n<p><img src="http://www.auditoriumlyon.com/onl_info/images/575_filet.jpg" alt="filet" width="575" height="20" /><br /><span class="date">Dimanche 21 octobre - 11h00</span><br /><span class="Style1">Musique de chambre</span></p>\r\n<p><span class="Style2">LUEURS DU CREPUSCULE</span></p>\r\n<p class="Style1"><strong>Wolfgang Amadeus Mozart</strong>&nbsp;Quintette pour cor et cordes, en mi b&eacute;mol majeur, KV 407<br /><strong>Johannes Brahms</strong>&nbsp;Quintette pour clarinette et cordes, en si mineur, op. 115</p>\r\n<p class="Style1">Musiciens de l&rsquo;Orchestre national de Lyon :&nb