INSERT INTO `evenement` (`id`, `nom`, `date`, `lieu`, `detail`, `auteur`) VALUES
(2, 'REQUIEM DE BERLIOZ', '2012-09-04', 'Auditorium Orchestre National‎ 149 Rue Garibaldi 69003 Lyon', '<p>Hector Berlioz Grande Messe des morts (Requiem)<br /><br />Orchestre national de Lyon<br />Steve Davislim, t&eacute;nor<br />Choral Arts Society of Washington<br />Norman Scribner, chef de choeur<br />Philharmonia Chorus<br />Stefan Bevier, chef de choeur associ&eacute;<br />Choeurs et Solistes de Lyon-Bernard T&eacute;tu (Choeur d&rsquo;oratorio de Lyon)<br />Catherine Molmerret, chef de choeur<br />Leonard Slatkin, direction</p>', 'Roques Steve'),
(3, 'REQUIEM DE BERLIOZ', '2012-09-05', 'Auditorium Orchestre National‎ 149 Rue Garibaldi 69003 Lyon', '<p>Hector Berlioz Grande Messe des morts (Requiem)<br /><br />Orchestre national de Lyon<br />Steve Davislim, t&eacute;nor<br />Choral Arts Society of Washington<br />Norman Scribner, chef de choeur<br />Philharmonia Chorus<br />Stefan Bevier, chef de choeur associ&eacute;<br />Choeurs et Solistes de Lyon-Bernard T&eacute;tu (Choeur d&rsquo;oratorio de Lyon)<br />Catherine Molmerret, chef de choeur<br />Leonard Slatkin, direction</p>', 'Roques Steve'),
(4, 'BALI DANSES ET DRAMES', '2012-09-14', 'Auditorium Orchestre National‎ 149 Rue Garibaldi 69003 Lyon', '<p>Bali danses et drames <br />Ce spectacle, nous plonge dans la culture balinaise, dans ce qu&rsquo;elle a de plus profond, de plus r&eacute;el et de plus authentique. Nous aurons la chance de d&eacute;couvrir ces arts chor&eacute;graphiques et dramatiques qui ont inspir&eacute; dans les ann&eacute;es trente des artistes aussi diff&eacute;rents que Charlie Chaplin, Benjamin Britten ou Antonin Artaud.</p>', 'Roques Steve'),
(5, 'BALI DANSES ET DRAMES', '2012-09-15', 'Auditorium Orchestre National‎ 149 Rue Garibaldi 69003 Lyon', '<p>Bali danses et drames <br />Ce spectacle, nous plonge dans la culture balinaise, dans ce qu&rsquo;elle a de plus profond, de plus r&eacute;el et de plus authentique. Nous aurons la chance de d&eacute;couvrir ces arts chor&eacute;graphiques et dramatiques qui ont inspir&eacute; dans les ann&eacute;es trente des artistes aussi diff&eacute;rents que Charlie Chaplin, Benjamin Britten ou Antonin Artaud.</p>', 'Roques Steve');

